src/CampaignFunding.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
 * @title CampaignFunding
 * @dev Smart contract for managing film campaign funding with escrow functionality
 * @author CineChainLanka
 */
contract CampaignFunding is ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    // Campaign structure
    struct Campaign {
        uint256 id;
        address creator;
        string title;
        string description;
        uint256 fundingGoal;
        uint256 currentFunding;
        uint256 startTime;
        uint256 endTime;
        bool isActive;
        bool isFunded;
        bool isFailed;
        uint256 backerCount;
        mapping(address => uint256) contributions;
        mapping(address => bool) hasContributed;
    }

    // Events
    event CampaignCreated(
        uint256 indexed campaignId,
        address indexed creator,
        string title,
        uint256 fundingGoal,
        uint256 startTime,
        uint256 endTime
    );
    
    event ContributionMade(
        uint256 indexed campaignId,
        address indexed backer,
        uint256 amount,
        uint256 totalFunding
    );
    
    event CampaignFunded(uint256 indexed campaignId, uint256 totalFunding);
    event CampaignFailed(uint256 indexed campaignId, uint256 totalFunding);
    event RefundProcessed(uint256 indexed campaignId, address indexed backer, uint256 amount);
    event FundsWithdrawn(uint256 indexed campaignId, address indexed creator, uint256 amount);

    // State variables
    mapping(uint256 => Campaign) public campaigns;
    uint256 public campaignCount;
    uint256 public platformFeePercentage = 300; // 3% in basis points
    address public platformWallet;
    IERC20 public usdtToken;
    
    // Campaign status constants
    uint256 public constant CAMPAIGN_ACTIVE = 1;
    uint256 public constant CAMPAIGN_FUNDED = 2;
    uint256 public constant CAMPAIGN_FAILED = 3;

    constructor(address _usdtToken, address _platformWallet) {
        usdtToken = IERC20(_usdtToken);
        platformWallet = _platformWallet;
    }

    /**
     * @dev Create a new campaign
     * @param _title Campaign title
     * @param _description Campaign description
     * @param _fundingGoal Funding goal in USDT (with 6 decimals)
     * @param _duration Campaign duration in seconds
     */
    function createCampaign(
        string memory _title,
        string memory _description,
        uint256 _fundingGoal,
        uint256 _duration
    ) external returns (uint256) {
        require(_fundingGoal > 0, "Funding goal must be greater than 0");
        require(_duration > 0, "Duration must be greater than 0");
        require(bytes(_title).length > 0, "Title cannot be empty");
        require(bytes(_description).length > 0, "Description cannot be empty");

        campaignCount++;
        uint256 campaignId = campaignCount;
        
        Campaign storage campaign = campaigns[campaignId];
        campaign.id = campaignId;
        campaign.creator = msg.sender;
        campaign.title = _title;
        campaign.description = _description;
        campaign.fundingGoal = _fundingGoal;
        campaign.currentFunding = 0;
        campaign.startTime = block.timestamp;
        campaign.endTime = block.timestamp.add(_duration);
        campaign.isActive = true;
        campaign.isFunded = false;
        campaign.isFailed = false;
        campaign.backerCount = 0;

        emit CampaignCreated(
            campaignId,
            msg.sender,
            _title,
            _fundingGoal,
            block.timestamp,
            campaign.endTime
        );

        return campaignId;
    }

    /**
     * @dev Contribute to a campaign
     * @param _campaignId Campaign ID
     * @param _amount Contribution amount in USDT
     */
    function contribute(uint256 _campaignId, uint256 _amount) external nonReentrant {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        require(_amount > 0, "Contribution amount must be greater than 0");
        
        Campaign storage campaign = campaigns[_campaignId];
        require(campaign.isActive, "Campaign is not active");
        require(block.timestamp >= campaign.startTime, "Campaign has not started");
        require(block.timestamp <= campaign.endTime, "Campaign has ended");
        require(!campaign.isFunded, "Campaign is already funded");
        require(!campaign.isFailed, "Campaign has failed");

        // Transfer USDT from backer to contract
        require(
            usdtToken.transferFrom(msg.sender, address(this), _amount),
            "USDT transfer failed"
        );

        // Update campaign state
        if (!campaign.hasContributed[msg.sender]) {
            campaign.backerCount = campaign.backerCount.add(1);
            campaign.hasContributed[msg.sender] = true;
        }
        
        campaign.contributions[msg.sender] = campaign.contributions[msg.sender].add(_amount);
        campaign.currentFunding = campaign.currentFunding.add(_amount);

        emit ContributionMade(_campaignId, msg.sender, _amount, campaign.currentFunding);

        // Check if campaign is fully funded
        if (campaign.currentFunding >= campaign.fundingGoal) {
            _markCampaignAsFunded(_campaignId);
        }
    }

    /**
     * @dev Mark campaign as funded and transfer funds to creator
     */
    function _markCampaignAsFunded(uint256 _campaignId) internal {
        Campaign storage campaign = campaigns[_campaignId];
        campaign.isActive = false;
        campaign.isFunded = true;

        // Calculate platform fee
        uint256 platformFee = campaign.currentFunding.mul(platformFeePercentage).div(10000);
        uint256 creatorAmount = campaign.currentFunding.sub(platformFee);

        // Transfer funds
        if (platformFee > 0) {
            usdtToken.transfer(platformWallet, platformFee);
        }
        usdtToken.transfer(campaign.creator, creatorAmount);

        emit CampaignFunded(_campaignId, campaign.currentFunding);
        emit FundsWithdrawn(_campaignId, campaign.creator, creatorAmount);
    }

    /**
     * @dev Mark campaign as failed and enable refunds
     */
    function markCampaignAsFailed(uint256 _campaignId) external onlyOwner {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        
        Campaign storage campaign = campaigns[_campaignId];
        require(campaign.isActive, "Campaign is not active");
        require(block.timestamp > campaign.endTime, "Campaign has not ended yet");
        require(!campaign.isFunded, "Campaign is already funded");

        campaign.isActive = false;
        campaign.isFailed = true;

        emit CampaignFailed(_campaignId, campaign.currentFunding);
    }

    /**
     * @dev Process refund for a specific backer
     * @param _campaignId Campaign ID
     * @param _backer Backer address
     */
    function processRefund(uint256 _campaignId, address _backer) external nonReentrant {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        
        Campaign storage campaign = campaigns[_campaignId];
        require(campaign.isFailed, "Campaign has not failed");
        require(campaign.contributions[_backer] > 0, "No contribution found");
        require(!campaign.hasContributed[_backer] || campaign.contributions[_backer] > 0, "Already refunded");

        uint256 refundAmount = campaign.contributions[_backer];
        campaign.contributions[_backer] = 0;

        require(usdtToken.transfer(_backer, refundAmount), "Refund transfer failed");

        emit RefundProcessed(_campaignId, _backer, refundAmount);
    }

    /**
     * @dev Process all refunds for a failed campaign
     * @param _campaignId Campaign ID
     * @param _backers Array of backer addresses
     */
    function processBulkRefunds(uint256 _campaignId, address[] memory _backers) external onlyOwner {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        
        Campaign storage campaign = campaigns[_campaignId];
        require(campaign.isFailed, "Campaign has not failed");

        for (uint256 i = 0; i < _backers.length; i++) {
            address backer = _backers[i];
            if (campaign.contributions[backer] > 0) {
                uint256 refundAmount = campaign.contributions[backer];
                campaign.contributions[backer] = 0;
                usdtToken.transfer(backer, refundAmount);
                emit RefundProcessed(_campaignId, backer, refundAmount);
            }
        }
    }

    /**
     * @dev Get campaign details
     * @param _campaignId Campaign ID
     */
    function getCampaign(uint256 _campaignId) external view returns (
        uint256 id,
        address creator,
        string memory title,
        string memory description,
        uint256 fundingGoal,
        uint256 currentFunding,
        uint256 startTime,
        uint256 endTime,
        bool isActive,
        bool isFunded,
        bool isFailed,
        uint256 backerCount
    ) {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        
        Campaign storage campaign = campaigns[_campaignId];
        return (
            campaign.id,
            campaign.creator,
            campaign.title,
            campaign.description,
            campaign.fundingGoal,
            campaign.currentFunding,
            campaign.startTime,
            campaign.endTime,
            campaign.isActive,
            campaign.isFunded,
            campaign.isFailed,
            campaign.backerCount
        );
    }

    /**
     * @dev Get backer's contribution amount
     * @param _campaignId Campaign ID
     * @param _backer Backer address
     */
    function getContribution(uint256 _campaignId, address _backer) external view returns (uint256) {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        return campaigns[_campaignId].contributions[_backer];
    }

    /**
     * @dev Check if backer has contributed to campaign
     * @param _campaignId Campaign ID
     * @param _backer Backer address
     */
    function hasContributed(uint256 _campaignId, address _backer) external view returns (bool) {
        require(_campaignId > 0 && _campaignId <= campaignCount, "Invalid campaign ID");
        return campaigns[_campaignId].hasContributed[_backer];
    }

    /**
     * @dev Update platform fee percentage (only owner)
     * @param _feePercentage New fee percentage in basis points
     */
    function updatePlatformFee(uint256 _feePercentage) external onlyOwner {
        require(_feePercentage <= 1000, "Fee cannot exceed 10%");
        platformFeePercentage = _feePercentage;
    }

    /**
     * @dev Update platform wallet address (only owner)
     * @param _newWallet New platform wallet address
     */
    function updatePlatformWallet(address _newWallet) external onlyOwner {
        require(_newWallet != address(0), "Invalid wallet address");
        platformWallet = _newWallet;
    }

    /**
     * @dev Emergency withdrawal function (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = usdtToken.balanceOf(address(this));
        require(balance > 0, "No tokens to withdraw");
        usdtToken.transfer(owner(), balance);
    }
}

src/CineChainNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";

/**
 * @title CineChainNFT
 * @dev NFT contract for film campaign contributions with royalty support
 * @author CineChainLanka
 */
contract CineChainNFT is ERC721, ERC721URIStorage, ERC721Enumerable, ERC2981, Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    // NFT structure
    struct NFTData {
        uint256 tokenId;
        uint256 campaignId;
        address creator;
        address owner;
        uint256 contributionAmount;
        uint256 royaltyPercentage;
        string metadataURI;
        uint256 mintedAt;
        bool isTransferable;
    }

    // Events
    event NFTCreated(
        uint256 indexed tokenId,
        uint256 indexed campaignId,
        address indexed creator,
        address owner,
        uint256 contributionAmount,
        string metadataURI
    );
    
    event RoyaltyUpdated(uint256 indexed tokenId, uint256 newRoyaltyPercentage);
    event TransferabilityUpdated(uint256 indexed tokenId, bool isTransferable);
    event BatchMinted(address indexed to, uint256[] tokenIds, uint256 campaignId);

    // State variables
    mapping(uint256 => NFTData) public nftData;
    mapping(uint256 => uint256) public campaignNFTCount; // Track NFTs per campaign
    mapping(address => uint256[]) public userNFTs; // Track user's NFTs
    
    uint256 private _tokenIdCounter;
    uint256 public maxSupply = 1000000; // Maximum supply of NFTs
    uint256 public defaultRoyaltyPercentage = 500; // 5% default royalty in basis points
    address public campaignFundingContract;
    address public platformWallet;
    
    // Royalty recipient mapping
    mapping(uint256 => address) public royaltyRecipients;

    constructor(
        string memory name,
        string memory symbol,
        address _campaignFundingContract,
        address _platformWallet
    ) ERC721(name, symbol) {
        campaignFundingContract = _campaignFundingContract;
        platformWallet = _platformWallet;
        _tokenIdCounter = 1;
    }

    /**
     * @dev Mint NFT for campaign contribution
     * @param to Recipient address
     * @param campaignId Campaign ID
     * @param contributionAmount Contribution amount
     * @param metadataURI IPFS metadata URI
     * @param royaltyPercentage Royalty percentage in basis points
     */
    function mintNFT(
        address to,
        uint256 campaignId,
        uint256 contributionAmount,
        string memory metadataURI,
        uint256 royaltyPercentage
    ) external onlyOwner nonReentrant returns (uint256) {
        require(to != address(0), "Invalid recipient address");
        require(contributionAmount > 0, "Contribution amount must be greater than 0");
        require(bytes(metadataURI).length > 0, "Metadata URI cannot be empty");
        require(royaltyPercentage <= 1000, "Royalty cannot exceed 10%");
        require(_tokenIdCounter <= maxSupply, "Maximum supply reached");

        uint256 tokenId = _tokenIdCounter;
        _tokenIdCounter++;

        // Create NFT data
        nftData[tokenId] = NFTData({
            tokenId: tokenId,
            campaignId: campaignId,
            creator: to, // For now, contributor is the creator
            owner: to,
            contributionAmount: contributionAmount,
            royaltyPercentage: royaltyPercentage,
            metadataURI: metadataURI,
            mintedAt: block.timestamp,
            isTransferable: true
        });

        // Update counters
        campaignNFTCount[campaignId]++;
        userNFTs[to].push(tokenId);

        // Set royalty recipient
        royaltyRecipients[tokenId] = to;

        // Mint the NFT
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, metadataURI);
        _setTokenRoyalty(tokenId, to, uint96(royaltyPercentage));

        emit NFTCreated(tokenId, campaignId, to, to, contributionAmount, metadataURI);

        return tokenId;
    }

    /**
     * @dev Batch mint NFTs for multiple contributions
     * @param to Recipient address
     * @param campaignId Campaign ID
     * @param contributionAmounts Array of contribution amounts
     * @param metadataURIs Array of metadata URIs
     * @param royaltyPercentages Array of royalty percentages
     */
    function batchMintNFTs(
        address to,
        uint256 campaignId,
        uint256[] memory contributionAmounts,
        string[] memory metadataURIs,
        uint256[] memory royaltyPercentages
    ) external onlyOwner nonReentrant returns (uint256[] memory) {
        require(to != address(0), "Invalid recipient address");
        require(
            contributionAmounts.length == metadataURIs.length &&
            metadataURIs.length == royaltyPercentages.length,
            "Array lengths must match"
        );
        require(contributionAmounts.length > 0, "Must mint at least one NFT");
        require(_tokenIdCounter + contributionAmounts.length - 1 <= maxSupply, "Maximum supply exceeded");

        uint256[] memory tokenIds = new uint256[](contributionAmounts.length);

        for (uint256 i = 0; i < contributionAmounts.length; i++) {
            require(contributionAmounts[i] > 0, "Contribution amount must be greater than 0");
            require(bytes(metadataURIs[i]).length > 0, "Metadata URI cannot be empty");
            require(royaltyPercentages[i] <= 1000, "Royalty cannot exceed 10%");

            uint256 tokenId = _tokenIdCounter;
            _tokenIdCounter++;

            // Create NFT data
            nftData[tokenId] = NFTData({
                tokenId: tokenId,
                campaignId: campaignId,
                creator: to,
                owner: to,
                contributionAmount: contributionAmounts[i],
                royaltyPercentage: royaltyPercentages[i],
                metadataURI: metadataURIs[i],
                mintedAt: block.timestamp,
                isTransferable: true
            });

            // Update counters
            campaignNFTCount[campaignId]++;
            userNFTs[to].push(tokenId);

            // Set royalty recipient
            royaltyRecipients[tokenId] = to;

            // Mint the NFT
            _safeMint(to, tokenId);
            _setTokenURI(tokenId, metadataURIs[i]);
            _setTokenRoyalty(tokenId, to, uint96(royaltyPercentages[i]));

            tokenIds[i] = tokenId;

            emit NFTCreated(tokenId, campaignId, to, to, contributionAmounts[i], metadataURIs[i]);
        }

        emit BatchMinted(to, tokenIds, campaignId);

        return tokenIds;
    }

    /**
     * @dev Update NFT royalty percentage
     * @param tokenId Token ID
     * @param newRoyaltyPercentage New royalty percentage in basis points
     */
    function updateRoyalty(uint256 tokenId, uint256 newRoyaltyPercentage) external {
        require(_exists(tokenId), "Token does not exist");
        require(newRoyaltyPercentage <= 1000, "Royalty cannot exceed 10%");
        require(
            msg.sender == ownerOf(tokenId) || msg.sender == owner(),
            "Not authorized to update royalty"
        );

        nftData[tokenId].royaltyPercentage = newRoyaltyPercentage;
        _setTokenRoyalty(tokenId, royaltyRecipients[tokenId], uint96(newRoyaltyPercentage));

        emit RoyaltyUpdated(tokenId, newRoyaltyPercentage);
    }

    /**
     * @dev Update NFT transferability
     * @param tokenId Token ID
     * @param isTransferable Whether NFT is transferable
     */
    function updateTransferability(uint256 tokenId, bool isTransferable) external {
        require(_exists(tokenId), "Token does not exist");
        require(
            msg.sender == ownerOf(tokenId) || msg.sender == owner(),
            "Not authorized to update transferability"
        );

        nftData[tokenId].isTransferable = isTransferable;

        emit TransferabilityUpdated(tokenId, isTransferable);
    }

    /**
     * @dev Override transfer function to check transferability
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721, ERC721Enumerable) {
        if (from != address(0) && to != address(0)) {
            require(nftData[tokenId].isTransferable, "NFT is not transferable");
        }
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    /**
     * @dev Override _afterTokenTransfer to update NFT data
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override {
        if (from != address(0) && to != address(0)) {
            nftData[tokenId].owner = to;
            
            // Update user NFT tracking
            if (from != address(0)) {
                _removeFromUserNFTs(from, tokenId);
            }
            if (to != address(0)) {
                userNFTs[to].push(tokenId);
            }
        }
        super._afterTokenTransfer(from, to, tokenId, batchSize);
    }

    /**
     * @dev Remove token from user's NFT list
     * @param user User address
     * @param tokenId Token ID to remove
     */
    function _removeFromUserNFTs(address user, uint256 tokenId) internal {
        uint256[] storage userTokens = userNFTs[user];
        for (uint256 i = 0; i < userTokens.length; i++) {
            if (userTokens[i] == tokenId) {
                userTokens[i] = userTokens[userTokens.length - 1];
                userTokens.pop();
                break;
            }
        }
    }

    /**
     * @dev Get NFT data
     * @param tokenId Token ID
     */
    function getNFTData(uint256 tokenId) external view returns (NFTData memory) {
        require(_exists(tokenId), "Token does not exist");
        return nftData[tokenId];
    }

    /**
     * @dev Get user's NFTs
     * @param user User address
     */
    function getUserNFTs(address user) external view returns (uint256[] memory) {
        return userNFTs[user];
    }

    /**
     * @dev Get campaign NFTs
     * @param campaignId Campaign ID
     */
    function getCampaignNFTs(uint256 campaignId) external view returns (uint256[] memory) {
        uint256 count = campaignNFTCount[campaignId];
        uint256[] memory tokenIds = new uint256[](count);
        uint256 index = 0;

        for (uint256 i = 1; i < _tokenIdCounter; i++) {
            if (nftData[i].campaignId == campaignId) {
                tokenIds[index] = i;
                index++;
                if (index >= count) break;
            }
        }

        return tokenIds;
    }

    /**
     * @dev Get total NFTs minted for a campaign
     * @param campaignId Campaign ID
     */
    function getCampaignNFTCount(uint256 campaignId) external view returns (uint256) {
        return campaignNFTCount[campaignId];
    }

    /**
     * @dev Get total supply
     */
    function totalSupply() public view override(ERC721Enumerable) returns (uint256) {
        return _tokenIdCounter - 1;
    }

    /**
     * @dev Override supportsInterface
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Override tokenURI
     */
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    /**
     * @dev Override _burn
     */
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    /**
     * @dev Update campaign funding contract address (only owner)
     * @param _newContract New contract address
     */
    function updateCampaignFundingContract(address _newContract) external onlyOwner {
        require(_newContract != address(0), "Invalid contract address");
        campaignFundingContract = _newContract;
    }

    /**
     * @dev Update platform wallet address (only owner)
     * @param _newWallet New wallet address
     */
    function updatePlatformWallet(address _newWallet) external onlyOwner {
        require(_newWallet != address(0), "Invalid wallet address");
        platformWallet = _newWallet;
    }

    /**
     * @dev Update maximum supply (only owner)
     * @param _newMaxSupply New maximum supply
     */
    function updateMaxSupply(uint256 _newMaxSupply) external onlyOwner {
        require(_newMaxSupply > _tokenIdCounter - 1, "Max supply must be greater than current supply");
        maxSupply = _newMaxSupply;
    }
}

